    --control-checked-bgColor-active: lighten(@accent-color, 5%) !important;
    --control-checked-bgColor-disabled: #6e7681;
    --control-checked-fgColor-rest: @base;
    --control-checked-fgColor-disabled: #010409;
    --control-checked-borderColor-rest: @accent-color !important;
    --control-checked-borderColor-hover: @accent-color !important;
    --control-checked-borderColor-active: @accent-color !important;
    --control-checked-borderColor-disabled: @overlay;
