    --controlKnob-bgColor-checked: @text;
    --controlKnob-borderColor-rest: @overlay;
    --controlKnob-borderColor-disabled: #21262db3;
    --controlKnob-borderColor-checked: @pine;
    --button-default-fgColor-rest: @text !important;
    --button-default-bgColor-rest: @overlay !important;
    --button-default-bgColor-hover: @overlay !important;
    --button-default-bgColor-active: @overlay !important;
    --button-default-bgColor-selected: @overlay !important;
    --button-default-bgColor-disabled: @surface !important;
    --button-default-borderColor-rest: @overlay !important;
    --button-default-borderColor-hover: @overlay !important;
    --button-default-borderColor-active: @overlay !important;
