    --button-primary-borderColor-rest: @foam !important;
    --button-primary-borderColor-hover: @foam !important;
    --button-primary-borderColor-active: @foam !important;
    --button-primary-borderColor-disabled: fade(@foam, 70%) !important;
    --button-primary-shadow-selected: 0px 0px 0px 0px #000;
    --button-invisible-fgColor-rest: @accent-color !important;
    --button-invisible-fgColor-hover: lighten(@accent-color, 10%) !important;
    --button-invisible-fgColor-disabled: #6e7681;
    --button-invisible-iconColor-rest: @muted;
    --button-invisible-iconColor-hover: #e6edf3;
    --button-invisible-iconColor-disabled: #6e7681;
    --button-invisible-bgColor-rest: #0000;
    --button-invisible-bgColor-hover: #b1bac41f;
    --button-invisible-bgColor-active: #b1bac433;
    --button-invisible-bgColor-disabled: fade(@overlay, 70%);
    --button-invisible-borderColor-rest: #0000;
    --button-invisible-borderColor-hover: #0000;
    --button-invisible-borderColor-disabled: fade(@overlay, 70%);
    --button-outline-fgColor-rest: #388bfd;
    --button-outline-fgColor-hover: #58a6ff;
    --button-outline-fgColor-active: @text;
    --button-outline-fgColor-disabled: #4493f880;
    --button-outline-bgColor-rest: #f0f6fc;
    --button-outline-bgColor-hover: @overlay;
    --button-outline-bgColor-active: #0d419d;
    --button-outline-bgColor-disabled: #0d1117;
    --button-outline-borderColor-hover: #f0f6fc1a;
    --button-outline-borderColor-selected: #f0f6fc1a;
    --button-outline-shadow-selected: 0px 0px 0px 0px #000;
    --button-danger-fgColor-rest: @love !important;
    --button-danger-fgColor-hover: @surface !important;
    --button-danger-fgColor-active: @surface !important;
    --button-danger-fgColor-disabled: fade(@love, 50%) !important;
    --button-danger-iconColor-rest: @love !important;
    --button-danger-iconColor-hover: @base !important;
    --button-danger-bgColor-rest: @overlay !important;
    --button-danger-bgColor-hover: @love !important;
    --button-danger-bgColor-active: darken(@love, 5%) !important;
    --button-danger-bgColor-disabled: @surface !important;
    --button-danger-borderColor-rest: @overlay !important;
    --button-danger-borderColor-hover: @love !important;
    --button-danger-borderColor-active: darken(@love, 5%) !important;
    --button-danger-shadow-selected: 0px 0px 0px 0px #000;
    --button-inactive-fgColor: #8b949e;
    --button-inactive-bgColor: #21262d;
    --button-star-iconColor: @gold !important;
    --buttonCounter-default-bgColor-rest: @overlay;
    --buttonCounter-invisible-bgColor-rest: @overlay;
    --buttonCounter-primary-bgColor-rest: #04260f33;
    --buttonCounter-outline-bgColor-rest: #051d4d33;
    --buttonCounter-outline-bgColor-hover: #051d4d33;
