      --yt-spec-yellow: @love !important;
      --yt-spec-black-pure-alpha-5: @subtle !important;
      --yt-spec-black-pure-alpha-10: @muted !important;
      --yt-spec-black-pure-alpha-15: fade(@base, 85%) !important;
      --yt-spec-black-pure-alpha-30: fade(@base, 70%) !important;
      --yt-spec-black-pure-alpha-60: fade(@base, 40%) !important;
      --yt-spec-black-pure-alpha-80: fade(@base, 20%) !important;
      --yt-spec-black-1-alpha-98: fade(@base, 2%) !important;
      --yt-spec-black-1-alpha-95: fade(@base, 5%) !important;
      --yt-spec-white-1-alpha-10: fade(@text, 90%) !important;
